import styled, { css } from 'styled-components'
import t from 'prop-types'
// import { darken } from 'polished'
import theme from '../../src/Utils/Theme'

const baseHeading = css`
    margin: 0;
    padding: 0;
    font-family: ${theme.fontCustom};
    font-weight: ${theme.fontBold};
    letter-spacing: 0;
    color: ${theme.colors.text01};
    text-decoration: none;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
`

export const HeroTitle = styled.h1`
    ${baseHeading}
    font-size: ${theme.fontSizes[11]};
    line-height: ${theme.lineHeight[8]};
`

export const Title = styled(HeroTitle)`
    font-size: ${theme.fontSizes[9]};
    line-height: ${theme.lineHeight[7]};
`

export const Heading2 = styled.h2`
    ${baseHeading}
    font-size: ${theme.fontSizes[8]};
    line-height: ${theme.lineHeight[7]};
`

export const Heading3 = styled.h3`
    ${baseHeading}
    font-size: ${theme.fontSizes[6]};
    line-height: ${theme.lineHeight[7]};
`

export const Heading4 = styled.h4`
    ${baseHeading}
    font-size: ${theme.fontSizes[5]};
    line-height: ${theme.lineHeight[7]};
`

export const Heading5 = styled.h5`
    ${baseHeading}
    font-size: ${theme.fontSizes[4]};
    line-height: ${theme.lineHeight[7]};
`

export const Heading6 = styled.h6`
    ${baseHeading}
    font-size: ${theme.fontSizes[3]};
    line-height: ${theme.lineHeight[7]};
    letter-spacing: .16px;
`

const scales = {
    small: `
        font-size: ${theme.fontSizes[3]};
        line-height: ${theme.lineHeight[2]};
        letter-spacing: .16px;
    `,
    normal: `
        font-size: ${theme.fontSizes[4]};
        line-height: ${theme.lineHeight[4]};
        letter-spacing: 0;
    `,
  }

const getScale = ({ scale = 'normal' }) => scales[scale]

const baseType = css`
    margin: 0;
    padding: 0;
    font-family: ${theme.fontDefault};
    font-weight: ${theme.fontRegular};
    color: ${theme.colors.text01};
    text-decoration: none;
`

export const Paragraph = styled.p`
    ${baseType}
    ${getScale}
`

export const ShortParagraph = styled(Paragraph)`
    line-height: ${theme.lineHeight[3]};
`

Paragraph.propTypes = {
    scales: t.oneOf(['small', 'normal']),
  }

Paragraph.defaultProps = {
    scales: 'normal'
}

HeroTitle.displayName = 'HeroTitle'
Title.displayName = 'Title'
Heading2.displayName = 'Heading2'
Heading3.displayName = 'Heading3'
Heading4.displayName = 'Heading4'
Heading5.displayName = 'Heading5'
Heading6.displayName = 'Heading6'
Paragraph.displayName = 'Paragraph'
ShortParagraph.displayName = 'ShortParagraph'
