import React from 'react'
import styled from 'styled-components'
// import t from 'prop-types'
// import theme from '../../Utils/Theme'
// import Skeleton from '../../Utils/Skeleton'

const InputTextWrapper = styled.div`
  display: flex;
  position: relative;
  flex-direction: column;
  width: 246px;
`

const InputTextLabel = styled.label`
  /* position: absolute; */
  font-size: 1rem;
  color: #429FAA;
  transition: all 150ms ease-in-out;
  opacity: 1;
  font-weight: bold;
  pointer-events: none;

  transform: ;
  transform: ${
      props => props.active ?
          "scale(0.95) translateX(9px) translateY(0px)" :
          "scale(1) translateX(16px) translateY(32px)"
  };
`

const InputTextStyled = styled.input`
  font-size: 1rem;
  border: 0;
  border-bottom: 2px solid #429FAA;
  outline: none;
  height: 40px;
  padding: 0.5rem 1rem;
  cursor: text;
`


class InputText extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      active: false
    };

    this.handleInput = this.handleInput.bind(this);
  }

  handleInput(evt) {
    if(evt.target.value !== '') {
      this.setState({active: true});
    }
    else {
      this.setState({active: false});
    }

    // run 
  }

  render() {
    return (
      <InputTextWrapper>
        <InputTextLabel active={this.state.active}>{this.props.placeholder}</InputTextLabel>
        <InputTextStyled onChange={this.handleInput} />
      </InputTextWrapper>
      );
  }

}


export default InputText
