import React from "react";
import styled from 'styled-components'
// import { darken } from 'polished'
import { HeroTitle, Title, Heading2, Heading3, Heading4, Heading5, Heading6, SubHeading, Paragraph } from '../../src/Utils/Typography'
import { typeScale, lineHeight, fontDefault, media } from '../../src/Utils/Theme'

const PostWrapper = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 4rem;

  ${media.md`
    flex-direction: row;
    align-items: flex-start;
    justify-content: space-between;
  `}
`;

const TokenOutputWrapper = styled.div`
    margin-bottom: 2rem;

    ${media.md`
        margin-bottom: 0;
        padding-right: 2rem;
        min-width: 517px;
        display: flex;
        align-items: center;
        justify-content: flex-start;
    `}
`

const TokenExample = styled.div`
`

const TokenInfo = styled.div`
    padding: 1rem 1rem 1.5rem;
    display: inline-block;
    background: #f8f8f8;
    border-radius: 4px;

    ${media.md`
        background: #fcfcfc;
        min-width: 18rem;
        transition: all 250ms ease-in-out;

        ${PostWrapper}:hover & {
            box-shadow: 0 12px 24px 0 rgba(0,0,0,0.1);
        }
    `}
`

const CodeExample = styled.code`
    margin-bottom: 10px;
    padding: 0 8px;
    display: inline-block;
    font-size: 0.875rem;
    color: #787878;
    background: #f0f0f0;
    border: 1px solid #cdcdcd;
    border-radius: 4px;
`

const TextDescWrapper = styled.ul`
    margin: 0;
    padding: 0 0.875rem;
    list-style: none;
`

const TextDesc = styled.li`
    padding: 0;
    margin: 0;
    font-size: 0.875rem;

    span {
        width: 104px;
        display: inline-block;
        font-weight: 500;
    }
`

const ParagraphSmall = styled(Paragraph)`
    font-size: ${typeScale[3]};
    line-height: ${lineHeight[2]};
    letter-spacing: .16px;
`


function TokenList(props) {
  const postContent = props.posts.map((post) =>
    <PostWrapper key={post.tokenVal}>
        <TokenOutputWrapper>
            <TokenExample as={post.tokenVal}>
                {post.tokenText}
            </TokenExample>
        </TokenOutputWrapper>
        <TokenInfo>
            <CodeExample>{post.tokenUsage}</CodeExample>
            <TextDescWrapper>
                <TextDesc>
                    <span>Font family:</span>
                    {post.fontFamily}
                </TextDesc>
                <TextDesc>
                    <span>Size:</span>
                    {post.sizeREM} / {post.sizePx}
                </TextDesc>
                <TextDesc>
                    <span>Line height:</span>
                    {post.lineHeightREM} / {post.lineHeightPX}
                </TextDesc>
                <TextDesc>
                    <span>Weight:</span>
                    {post.weight}
                </TextDesc>
                <TextDesc>
                    <span>Letter spacing:</span>
                    {post.letterSpacing}
                </TextDesc>
            </TextDescWrapper>
        </TokenInfo>
    </PostWrapper>
  );
  return (
    <React.Fragment>
      {postContent}
    </React.Fragment>
  );
}


const posts = [
  {
    tokenVal: HeroTitle,
    tokenUsage: '<HeroTitle>{text}</HeroTitle>',
